const nextTopicButton = document.getElementById("next-topic-button");
const topic = document.getElementById("topic");
const thinkingTime = document.getElementById("thinking-time");
const thinkingTimeProgress = document.getElementById("thinking-time-progress");
const speakingTime = document.getElementById("speaking-time");
const speakingTimeProgress = document.getElementById("speaking-time-progress");

let currentInterval = undefined;

const topics = [
    "Some students study for classes individually. Others study in groups. <strong>Which method of studying do you think is better for students and why?</strong>",
    "Do you agree or disagree with the following statement: <strong>It is important to learn about other cultures</strong>",
    "Do you agree or disagree with the following statement? <strong>Children should help their parents with household chores as soon as they are old enough. Use details and examples in your response.</strong>",
    "Do you agree or disagree with the following statement?  <strong>It is better for children to have teachers who are young, even if they are inexperienced.</strong>",
    "Do you agree or disagree with the following sentence?  <strong>We can learn a lot from making mistakes.</strong>"
];

window.onload = function(){
    nextTopicButton.addEventListener("click", nextTopic);
    nextTopic();
};

function cancelCurrentInterval() {
    clearInterval(currentInterval);
}

function randomTopic() {
    return topics[Math.floor(Math.random()*topics.length)];
}

function showThinkingTime() {
    thinkingTime.style.display = 'block';
    speakingTime.style.display = 'none';
}

function showSpeakingTime() {
    thinkingTime.style.display = 'none';
    speakingTime.style.display = 'block';
}

function resetTimers() {
    setProgressbarToMaxValue(thinkingTimeProgress);
    setProgressbarToMaxValue(speakingTimeProgress);
}


function getProgressBarMaxValue(progressBar) {
    return progressBar.getAttribute('aria-valuemax');
}

function setProgressBarValue(progressBar, newValue) {
    let newWidth = 100 / getProgressBarMaxValue(progressBar) * newValue;
    progressBar.setAttribute('aria-valuenow',newValue);
    progressBar.setAttribute('style','width:'+Number(newWidth)+'%');
}

function setProgressbarToMaxValue(progressBar) {
    setProgressBarValue(progressBar, getProgressBarMaxValue(progressBar));
}

function startTimerProgressBar(progressBar, callback) {
    beep();
    currentInterval = setInterval(function () {

        const newValue = progressBar.getAttribute('aria-valuenow') -1;

        if (newValue < 0) {
            cancelCurrentInterval();
            if (callback != undefined) {
                callback();
            }
        } else {
            setProgressBarValue(progressBar, newValue);
        }
    }, 1000);
}

function beep() {
    let sound = new Audio("data:audio/wav;base64,");
    sound.play();
}

function nextTopic() {
    cancelCurrentInterval();
    topic.innerHTML = randomTopic();
    showThinkingTime();
    resetTimers();
    startTimerProgressBar(thinkingTimeProgress, () => {
        showSpeakingTime();
        startTimerProgressBar(speakingTimeProgress);
    })
}
